import "./admin.css";
import { useState } from "react";

const Admin = () => {
    const [product, setProduct] = useState({});
    const [coupon, setCoupon] = useState({});
    const [allCoupons, setAllCoupons] = useState([]);


    const saveProduct= () => {
        console.log("Saving product");
    };

    const textChanged = (e) => {
        let name = e.target.name; 
        let val = e.target.value;

        let copy = { ...product };
        copy[name] = val;
        setProduct(copy);

        // TODO: send the product to service -> to backend
    };

    const couponChanged = (e) => {
        let name=e.target.name;
        let val=e.target.value;

        let copy= {...coupon};
        copy[name]=val;
        setCoupon(copy);
    };

    const saveCoupon = () => {
        console.log("Saving coupon", coupon);

        // add the coupon to allCoupons array
        let copy=[...allCoupons];
        copy.push(coupon);
        setAllCoupons(copy);

        // todo: send the coupon to service -> backend
    };

    return (
        <div className="admin-page">
            <h4>Store Adminitration</h4>

            <div className="content">
                <section className="products">
                    <h5>Register new Product</h5>

                        <div className="field">
                            <label>Title</label>
                            <input name="title" onChange= {textChanged} className="form-control" type="text"/>
                        </div>

                        <div className="field">
                            <label>Category</label>
                            <input name="category" onChange= {textChanged} className="form-control" type="text"/>
                        </div>

                        <div className="field">
                            <label>Image</label>
                            <input className="form-control" type="text" placeholder="image name" />
                        </div>

                        <div className="field">
                            <label>Price</label>
                            <input name="price" onChange={textChanged} className="form-control" type="number" />
                        </div>

                        <div className="field">
                            <button onClick={saveProduct} className="btn   btn-primary">
                                Register Product
                            </button>
                        </div>
                </section>

                    <section className="coupons">
                        <h5>Coupon Codes</h5>

                        <div className="field">
                            <label>Code</label>
                            <input name="code" onChange={couponChanged} className="form-control" type="text" />
                        </div>

                        <div className="field">
                            <label>Discount</label>
                            <input name="discount" onChange={couponChanged} className="form-control" type="number" />
                        </div>

                        <div className="field">
                            <button onClick={saveProduct} className="btn   btn-primary">
                                Register Product
                            </button>
                        </div>

                     <hr />
                     <ul>
                        <li>Code - Discount</li>
                        <li>Code - Discount</li>
                        <li>Code - Discount</li>
                        <li>Code - Discount</li>
                        <li>Code - Discount</li>
                        <li>Code - Discount</li>
                     </ul>

                    </section>
            </div>
        </div>
    );
};

export default Admin;
